import React, { useState } from 'react';
import MobileStepper from '@mui/material/MobileStepper';
import KeyboardArrowLeft from '@mui/icons-material/KeyboardArrowLeft';
import KeyboardArrowRight from '@mui/icons-material/KeyboardArrowRight';
import Button from '@mui/material/Button';
import { useTheme } from '@mui/material/styles';
import { useSwipeable } from 'react-swipeable';
import '../../styles/swipe.css';

function ImageStepper() {
    const theme = useTheme();

    // public 안에 저장된 이미지 목록
    const images = [
        '/landing/4.png',
        '/landing/5.png',
    ];

    const [activeStep, setActiveStep] = React.useState(0);
    const [swipeDirection, setSwipeDirection] = useState('');

    // 버튼 조작하여 이미지 넘기기
    const handleNext = () => {
        setSwipeDirection('left');
        setActiveStep((prevActiveStep) => prevActiveStep + 1);
    };
    const handleBack = () => {
        setSwipeDirection('right');
        setActiveStep((prevActiveStep) => prevActiveStep - 1);
    };

    const swipeHandlers = useSwipeable({
        onSwipedLeft: () => {
            if (activeStep < images.length - 1) handleNext();
        },
        onSwipedRight: () => {
            if (activeStep > 0) handleBack();
        },
        preventDefaultTouchmoveEvent: true,
        trackMouse: true, // 마우스 드래그도 가능하게 설정
    });

    return(
        <div {...swipeHandlers}>
            <img
                src={images[activeStep]}
                alt={`${activeStep + 1}`}
                className={`image ${swipeDirection}`}
                loading="lazy"
            />

            <MobileStepper
                style={{marginTop:'5%'}}
                variant="dots"
                steps={images.length}
                position="static"
                activeStep={activeStep}
                nextButton={
                    <Button size="small" onClick={handleNext} disabled={activeStep === images.length - 1}>
                        다음
                    {theme.direction === 'rtl' ? (
                        <KeyboardArrowLeft />
                    ) : (
                        <KeyboardArrowRight />
                    )}
                    </Button>
                }
                backButton={
                    <Button size="small" onClick={handleBack} disabled={activeStep === 0}>
                    {theme.direction === 'rtl' ? (
                        <KeyboardArrowRight />
                    ) : (
                        <KeyboardArrowLeft />
                    )}
                    이전
                </Button>
              }
            />
        </div>
    );
}

export default ImageStepper;
