import React from 'react';
import { useTheme } from '@mui/material/styles';
import BackgroundBox from '../../components/common/BackgroundBox';
import Typography from '@mui/material/Typography';
import MobileStepper from '@mui/material/MobileStepper';
import Button from '@mui/material/Button';
import KeyboardArrowLeft from '@mui/icons-material/KeyboardArrowLeft';
import KeyboardArrowRight from '@mui/icons-material/KeyboardArrowRight';


function Landing() {
    const theme = useTheme();
    const images = [
        '/landing/5.png',
    ];

    const [activeStep, setActiveStep] = React.useState(0);
    const [isLandscape, setIsLandscape] = useState(window.innerWidth > window.innerHeight);

    const handleNext = () => {
        setActiveStep((prevActiveStep) => prevActiveStep + 1);
    };

    const handleBack = () => {
        setActiveStep((prevActiveStep) => prevActiveStep - 1);
    };

    // 화면 크기 변경 시 이미지 비율 업데이트
    useEffect(() => {
        const handleResize = () => {
            setIsLandscape(window.innerWidth > window.innerHeight);
        };

        window.addEventListener('resize', handleResize);
        return () => {
            window.removeEventListener('resize', handleResize);
        };
    }, []);

    const imgStyle = isLandscape
        ? { width: '70%', height: 'auto', marginTop: '15%' }
        : { width: 'auto', height: '70%', marginTop: '15%' };

    return (
        <BackgroundBox>
            <Typography variant='h1' style={{marginTop:'20%', whiteSpace: 'nowrap'}}>
                산들바람 길잡이
            </Typography>

            <img
                src={images[activeStep]}
                alt={`${activeStep + 1}`}
                loading="lazy"
                style={{marginTop:'15%', width: '70%'}}
            />

            <MobileStepper
                style={{marginTop:'5%'}}
                variant="dots"
                steps={images.length}
                position="static"
                activeStep={activeStep}
                nextButton={
                    <Button size="small" onClick={handleNext} disabled={activeStep === images.length - 1}>
                        다음
                    {theme.direction === 'rtl' ? (
                        <KeyboardArrowLeft />
                    ) : (
                        <KeyboardArrowRight />
                    )}
                    </Button>
                }
                backButton={
                    <Button size="small" onClick={handleBack} disabled={activeStep === 0}>
                    {theme.direction === 'rtl' ? (
                        <KeyboardArrowRight />
                    ) : (
                        <KeyboardArrowLeft />
                    )}
                    이전
                </Button>
              }
            />

            <Button style={{marginTop:'5%', marginBottom: '15%'}}>
                시작하기
            </Button>
        </BackgroundBox>
    );
}

export default Landing;
