import React, { useEffect, useState } from 'react';
import { useTheme } from '@mui/material/styles';
import BackgroundBox from '../../components/common/BackgroundBox';
import Typography from '@mui/material/Typography';
import Button from '@mui/material/Button';


function Landing() {
    const [isLandscape, setIsLandscape] = useState(window.innerWidth > window.innerHeight);

    // 화면 크기 변경 시 이미지 비율 업데이트
    useEffect(() => {
        const handleResize = () => {
            setIsLandscape(window.innerWidth > window.innerHeight);
        };

        window.addEventListener('resize', handleResize);
        return () => {
            window.removeEventListener('resize', handleResize);
        };
    }, []);

    const imgStyle = isLandscape
        ? { width: 'auto', height: '60%', marginTop: '5%' }
        : { width: '50%', height: 'auto', marginTop: '15%' };

    return (
        <BackgroundBox>
            <Typography variant='h1' style={{whiteSpace: 'nowrap', marginTop:'5%'}}>
                산들바람 길잡이
            </Typography>

            <Button style={{marginTop:'5%', marginBottom: '10%'}}>
                시작하기
            </Button>
        </BackgroundBox>
    );
}

export default Landing;
